/* MyImageView */#import <Cocoa/Cocoa.h>@class		MyDocument;@interface MyImageView : NSImageView{    IBOutlet MyDocument			*myDocument;    IBOutlet NSTextField		*alphaText;    IBOutlet NSTextField		*compositeModeText;    IBOutlet NSTextField		*densityText;    IBOutlet NSTextField		*remanenzText;    IBOutlet NSTextField		*timeText;    IBOutlet NSTextField		*triggerText;    IBOutlet NSTextField		*whiteText;    BOOL 						running;					// whether we're currently drawing    NSTimer 					*repeatTimer;				// timer that controls how often we draw}- (IBAction)			changeStart:(id)sender;@end